import React, { Component } from 'react'
import '../../../Css/Account.css'

class Account extends Component {
    constructor(){
        super();
        this.state={
            name:"Aiswarya Jayachandran",
            DOB: "24-05-20",
            email :"aiswarya@gmail.com",
            phone : "2123445444"
        }
        this.dataChanged = this.dataChanged.bind(this);

    }

    dataChanged(data) {
        // data = { description: "New validated text comes here" }
        // Update your model from here
        console.log(data)
        this.setState({...data})
    }
    render() {
        return (
            <div id="main-content" >
            <h3 id="account-title">Account Details</h3>


            <div className="col-md-5">
            <div className="form-area">  
              <form role="form">
                <div className="form-group">
                   <p></p> <input id="name" value={this.state.name} type="text" onChange={this.handleNameChange} className="form-control" placeholder="Name" required />
                </div>
                <div className="form-group">
                  <input value={this.state.DOB} type="text" onChange={this.handlePasswordChange} className="form-control" placeholder="Password" required />
                </div>
                <div className="form-group">
                  <input value={this.state.email} type="text" onChange={this.handlePasswordChange} className="form-control" placeholder="Password" required />
                </div>
                <div className="form-group">
                  <input value={this.state.phone} type="text" onChange={this.handlePasswordChange} className="form-control" placeholder="Password" required />
                </div>
                
                <button type="button" onClick={this.updateProfile} id="submit" name="submit" className="btn btn-primary pull-right">Update</button>
              </form>
          </div>
        </div>

            </div>
        )
    }
}

export default Account
