import React, { Component } from 'react'
import '../../../Css/Account.css'

class Account extends Component {
    constructor(){
        super();
        this.state={
            name:"Aiswarya Jayachandran",
            DOB: "24-05-20",
            email :"aiswarya@gmail.com",
            phone : "2123445444"
        }
        this.dataChanged = this.dataChanged.bind(this);

    }

    dataChanged(data) {
        // data = { description: "New validated text comes here" }
        // Update your model from here
        console.log(data)
        this.setState({...data})
    }
    render() {
        return (
            <div id="main-content" >
                <div className="col-md-12">
                    <div className="form-area"> 
                    <h3  id="account-title">Account Details</h3>
    
                        <form id="account-form">

                            <div className="form-group">
                            <label for="name">Name:</label>

                            <input id="name" value={this.state.name} type="text" onChange={this.handleNameChange} className="form-control" placeholder="Name"  />
                            <label for="email">Email:</label>

                            <input  id="email" value={this.state.DOB} type="text" onChange={this.handlePasswordChange} className="form-control" placeholder="Password" />
                            <label for="DOB">DOB:</label>

                            <input id="DOB" value={this.state.email} type="text" onChange={this.handlePasswordChange} className="form-control" placeholder="Password" />
                            <label for="phone">Phone:</label>

                            <input id="phone" value={this.state.phone} type="text" onChange={this.handlePasswordChange} className="form-control" placeholder="Password"  />
                            </div>
                            
                            <button  id="account-button" type="button" onClick={this.updateProfile} id="submit" name="submit" className="btn btn-primary pull-right">Update</button>
                        </form>
                    </div>
                    </div>

            </div>
        )
    }
}

export default Account
